CREATE TABLE mission ("id" BIGINT NOT NULL PRIMARY KEY GENERATED ALWAYS AS IDENTITY,"code" VARCHAR(255),"location" VARCHAR(255),"start" TIMESTAMP,"end" TIMESTAMP,"objective" VARCHAR(255),"outcome" VARCHAR(255) );
CREATE TABLE agent ("id" BIGINT NOT NULL PRIMARY KEY GENERATED ALWAYS AS IDENTITY,"name" VARCHAR(255),"born" DATE,"died" DATE,"level" SMALLINT);
CREATE TABLE involvement ("id" BIGINT NOT NULL PRIMARY KEY GENERATED ALWAYS AS IDENTITY,"agent" BIGINT REFERENCES AGENT("id"),"mission" BIGINT REFERENCES MISSION("id"),"start" TIMESTAMP,"end" TIMESTAMP);